package com.shs.basics;

public class IfExample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//check for the condition if condition is true then executes stmt inside if condition otherwise executes
		//the stmt of else block
		int a=20;
		if(a<=18) {
			System.out.println("A is lesser value than 18");
		}
		else {
			System.out.println("A is greater than 18");
		}
	}

}
